// In App.js in a new project

import * as React from 'react';
import order from './assets/data/orders.json';
import { View, Text, FlatList,StyleSheet } from 'react-native';
import {LoGo, Backarrow} from './assets/index';
import { getFocusedRouteNameFromRoute, NavigationContainer, useNavigation } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Logo from './assets/svg';
import { MaterialCommunityIcons } from '@expo/vector-icons';


function HomeScreen() {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
     <svg width="22" height="22" viewBox="0 0 22 22" fill="none" xmlns="http://www.w3.org/2000/svg">
<Path d="M7.08615 6.30731L17.5575 21.8845C13.7529 21.1405 2.23773 18.2756 0.202186 11.1151C-1.12865 6.43354 4.44719 5.49724 7.08615 6.30731Z" fill="#00A4A4"/>
<Path d="M5.14699 8.71122C5.98929 11.0325 9.40047 14.2101 17.5575 21.8846L7.08614 6.30732C7.08614 6.30732 4.18983 6.07342 5.14699 8.71122Z" fill="#7CF7F7"/>
<Path d="M7.08624 6.30737L17.5576 21.8846C18.309 18.1117 19.9729 6.18327 14.0672 1.59572C10.4746 -1.19501 7.33314 3.5787 7.08624 6.30737Z" fill="#32E0E0"/>
</svg>
    </View>
  );
}

function ConsultScreen() {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Consult Screen</Text>
    </View>
  );
}

const ListHead = ()=>{
  return(
    <View>
      <Text style={styles.header}>Submitted orders{"\n"}{"\n"}</Text>
      <Text style={styles.subhead}>Pending orders</Text>
    </View>
  )
}

function OrderScreen() {
  return (
    <View style={styles.container}>
          <FlatList
      keyExtractor={(item) => item.orderID}
      data={order}
      ListHeaderComponent ={ListHead}
      renderItem={({ item }) => (
        
        <Text style={styles.item}>
        <Text>{item.orderStatus}{"\n"}</Text>
        <Text style={{fontWeight: "bold"}}>{item.pharmacyName}{"\n"}</Text>
        <Text>Sum total: R{item.orderTotal}</Text>
        </Text>
            )}
      />
    </View>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor:'white',
    justifyContent: 'center',
    alignItems: 'center'
  },
  item:{
    marginTop:24,
    padding:30,
    fontSize:24,
    backgroundColor:'lightblue'
  },
  header:{
    fontSize: 30,
    fontWeight: "bold"
  },
  subhead:{
    fontSize: 18,
    fontWeight: "bold"
  }
});
function ProfileScreen() {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Profile Screen</Text>
    </View>
  );
}

const Tab = createBottomTabNavigator();
const Stack = createNativeStackNavigator();

function App() {
  return (
    <NavigationContainer>
      <Tab.Navigator
      screenOptions={({route}) => ({
        tabBarIcon: ({focused, size, color}) => {
          let iconName;
          if (route.name === "Home") {
            iconName = focused ? 'home' : 'home';
          } else if (route.name === "Consults") {
            iconName = focused ? 'message' : 'message';
          } else if (route.name === "Orders") {
            iconName = focused ? 'cart' : 'cart';
          } else if (route.name === "Profile") {
            iconName = focused ? 'account-cog' : 'account-cog';
          }
          return <MaterialCommunityIcons name={iconName} size={size} color={color} />
        }
      })}
      >
        <Stack.Screen name="Home" component={HomeScreen} /> 
        <Stack.Screen name="Consults" component={ConsultScreen} />
        <Stack.Screen name="Orders" component={OrderScreen} />
        <Stack.Screen name="Profile" component={ProfileScreen} />
    </Tab.Navigator>
    </NavigationContainer>
  );
}

export default App;
